import React, { useState, useEffect } from "react";
import humidityImage from "./humidity.png";
import windImage from "./wind.png";
import searchImage from "./search.png";

export default function Temp() {
	const [data, setData] = useState(null);
	const [inputValue, setInputValue] = useState("");
	const API_KEY = "bb97b85f1902e6a1013af65e275e051d";
	// const [loading, setLoading] = useState(true);
	useEffect(() => {
		const fetchData = async () => {
			try {
				const url = await fetch(
					`https://api.openweathermap.org/data/2.5/weather?q=London&appid=${API_KEY}`
				);
				const jsonData = await url.json();
				setData(jsonData);
				// setLoading(false);
			} catch (error) {
				console.error("Error fetching data:", error);
			}
		};

		fetchData();
	}, []);
	console.log(data);

	const handleInputValue = (e) => {
		setInputValue(e.target.value);
		console.log(e.target.value);
	};
	const handleCity = async () => {
		console.log(inputValue);
	};
	return (
		<div>
			<input
				type="text"
				placeholder="Search City"
				value={inputValue}
				onChange={handleInputValue}
				className="w-full h-10 placeholder-opacity-50 rounded-2xl opacity-70 p-4"
			></input>
			{/* <label className="before:' ' after:' ' pointer-events-none absolute left-0 -top-1.5 flex h-full w-full select-none !overflow-visible truncate text-[11p] font-normal leading-tight text-grey-500 transition-all before:pointer-events-"></label> */}
			<button className="absolute inset-y-0 right-0 px-3 py-1 m-3 h-8 rounded-full bg-blue-500   hover:bg-blue-800 ">
				<img src={searchImage} alt="search" className="size-6" />
			</button>
			<img
				src={humidityImage}
				alt="temp"
				className=" flex justify-center items-center px-24 py-1.5 pt-10"
				onClick={handleCity}
			/>
			{data && <h1>{data.name}</h1>}
			{data && (
				<div className="flex flex-row  justify-between p-2">
					<div className="flex flex-col">
						<img
							src={humidityImage}
							alt="Humidity"
							className="flex1 size-8 ml-8"
						/>
						<p className="flex1">Humidity:{data.main.humidity}</p>
					</div>
					<div className="flex flex-col ">
						<img
							src={windImage}
							alt="wind"
							className="flex1 size-8 ml-10"
						/>
						<p className="flex1 ">Windspeed: {data.wind.speed}</p>
					</div>
				</div>
			)}
			<button className=" bg-blue-500/60  border border-black rounded-md hover:bg-blue-700 active:bg-blue-950">
				Readmore
			</button>
		</div>
	);
}
