import React, { useState, useEffect } from "react";
import humidityImage from "./humidity.png";
import windImage from "./wind.png";
import Search from "./Search";

export default function Temp({ inputValue }) {
	const [data, setData] = useState(null);
	const API_KEY = "abeed709880f4312ba274704241302";
	const loc = inputValue;
	// const [loading, setLoading] = useState(true);
	useEffect(() => {
		const fetchData = async () => {
			try {
				const url = await fetch(
					`http://api.weatherapi.com/v1/current.json?key=${API_KEY}&q=${loc}&aqi=no`
				);
				const jsonData = await url.json();
				setData(jsonData);
				// setLoading(false);
			} catch (error) {
				console.error("Error fetching data:", error);
			}
		};

