import React, { useState, useEffect } from "react";
import humidityImage from "./humidity.png";
import windImage from "./wind.png";
import Search from "./Search";

export default function Temp({ inputValue }) {
	const [data, setData] = useState(null);
	const API_KEY = "abeed709880f4312ba274704241302";
	const location = inputValue;
	// const [loading, setLoading] = useState(true);
	useEffect(() => {
		const fetchData = async () => {
			try {
				const url = await fetch(
					`http://api.weatherapi.com/v1/current.json?key=${API_KEY}&q=${location}&aqi=no`
				);
				const jsonData = await url.json();
				setData(jsonData);
				// setLoading(false);
			} catch (error) {
				console.error("Error fetching data:", error);
			}
		};

		fetchData();
	}, []);
	console.log(data);

	return (
		<div>
			<Search></Search>
			{data && <h1>{data.name}</h1>}
			{data && (
				<div className="flex flex-row  justify-between p-2">
					<div className="flex flex-col">
						<img
							src={humidityImage}
							alt="Humidity"
							className="flex1 size-8 ml-8"
						/>
						<p className="flex1">Humidity:</p>
					</div>
					<div className="flex flex-col ">
						<img
							src={windImage}
							alt="wind"
							className="flex1 size-8 ml-10"
						/>
						<p className="flex1 ">Windspeed: </p>
					</div>
				</div>
			)}
		</div>
	);
}
