import React from "react";
import humidityImage from "./humidity.png";
import searchImage from "./search.png";
import windImage from "./wind.png";
const imgUrl = require("./sunrise.jpg");
export default function Display() {
	return (
		<>
			<div className=" grid justify-center items-center h-screen ">
				{/* <input
					type="text"
					className=" border border-sky-200	 fill-sky-300 px-4 py-2"
					placeholder="Enter something..."
				/> */}
				<div
					className=" bg-origin-border   box-content size-72  p-3  rounded-md  backdrop-blur-sm bg-white/30"
					// bg-gradient-to-b from-sky-700  to-sky-300 #for bg color gradient
					//h-96 w-96   md:h-64 md:w-64 #for responsive size of the box
					style={{
						backgroundImage: `URL(${imgUrl})`,
						backgroundSize: "cover",
					}}
				>
					<div className=" backdrop-opacity-10 backdrop-invert   bg-white/30 h-full w-full rounded-2xl  p-2 ">
						<input
							type="text"
							placeholder="Input text"
							className="w-full h-10 placeholder-opacity-50 rounded-2xl opacity-70 p-4"
						></input>
						<button className="absolute inset-y-0 right-0 px-3 py-1 m-3 h-8 rounded-full bg-blue-500   hover:bg-blue-800 ">
							<img
								src={searchImage}
								alt="search"
								className="size-10"
							/>
						</button>
						<div className="box-content p-8 text-5xl">ðŸ”†</div>
						<h2 className="location ">Nizamabad</h2>
						<div className="flex flex-row justify-between">
							<div className="flex flex-col">
								<img
									src={humidityImage}
									alt="Humidity"
									className="flex1 size-10"
								/>
								<p className="flex1">Humidity</p>
							</div>
							<div className="flex flex-col">
								<img
									src={windImage}
									alt="wind"
									className="flex1 size-10"
								/>
								<p className="flex1">Wind speed</p>
							</div>
						</div>
					</div>
				</div>
			</div>
		</>
	);
}
